import React from 'react'
import { Link } from "react-router-dom"
import { f_abi, to } from '../config'
import Web3 from 'web3'


const TrackCalorie = props => {

     const [State, setState] = useState({
        account: '',todo:null
    })
    
    const [data, setData] = useState({
    	id: '',
        calories:''
    })
    
    const {name,calories} = data
    
    const onClick = e => ({...data,[e.target.id]:e.target.value})
    
    
    async function load () {
        const web3 = new Web3(Web3.givenProvider || "HTTP://127.0.0.1:7545")
        const todo = new web3.eth.Contract(f_abi, to)
       
        const accounts = await web3.eth.getAccounts()
        setState({...State,todo:todo })
        setState({...State,account:accounts[0]})
         const pay = await todo.methods.transfer(State.account, web3.utils.toWei('50','ether'),calories , id) 
        
    }
    const onSubmit = e => {

    		e.preventDefault()
            load()
            
    }
    return (
        <div className="row">
            <div className="col"></div>
            <div className="col">
                <br/><br/>
                <h1> Calories Burnt Update </h1>
                <form>
                    <div class="mb-3">
                        <label for="exampleInputEmail1" class="form-label">id</label>
                        <input type="text"onChange={e => onClick(e)} class="form-control" id="name" aria-describedby="emailHelp" />
                    </div>
                    <div class="mb-3">
                        <label for="exampleInputPassword1" class="form-label">Calories</label>
                        <input type="text" onChange={e => onClick(e)} class="form-control" id="height" />
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button><br/><br/>
                    <Link to="/"><button type="submit" class="btn btn-primary">Home</button></Link>
                </form>
            </div>
            <div className="col"></div>
        </div>
    )
}

export default TrackCalorie
